import Link from 'next/link';
import { getPosts } from '../api/index';

const PostPage = ({ post }) =>{
  return(
    <div>
      <Link href="/">回上一頁</Link>
      <h1>
      data
      </h1>
    </div>

  )
}


PostPage.getInitialProps = async ({ req }) => {
  const res = await getPosts()
  const data = await res.json()
  return data;

}

export default PostPage
