import { Card ,Tag  } from 'antd';
import Link from 'next/link';
//import { Link } from '../routes'
import "../assets/styles.less";

const cardModule = ({post}) => {
  return(
    //  <Link route='post' params={{ id: post.id }}>
     <Link href="/[id]" as={`/${post.id}`}>
      <Card
        hoverable
        key={ post.id }
        title={ post.name }
        className="card mb-50"
        >
        <ul>
          <li><span className="content-p">景點地址:</span>{ post.address }</li>
          <li><span className="content-p">營業時間:</span>{ post.open_time ? post.open_time:'無' }</li>
          <li><span className="content-p">景點分類:</span>{ post.category.map(e => <Tag color="magenta">{ e.name }</Tag>) }
          </li>
        </ul>
      </Card>
    </Link>
  )
}

