import { Icon , Row , Col , Layout , Card } from 'antd';
import fetch from 'isomorphic-unfetch';
import Link from 'next/link';
import "../assets/styles.less";
import Carousel from '../components/carousel';
//import Card from '../components/card'
import WeatherAPP from '../components/weather';
const { Header, Content } = Layout;

const Index = props  => (
  <Layout>
    <Header>
      <Row justify="space-around" type="flex">
        <Col span={20}>
          <Row justify="space-around" type="flex">
            <Col span={12} md={12} xs={24}>
              <span className="ml-30 float-left"><Link href="/"><a>Welcome to Dora  Blog</a></Link></span>
            </Col>
            <Col span={12} md={12} xs={0}>
              <div className="ml-30 float-left">>
                <Link href="/about">
                  <a>About us</a>
                </Link>
              </div>
              <div className="ml-30 float-left">>
                <Link href="/blog">
                  <a>Blog</a>
                </Link>
              </div>
            </Col>
          </Row>
        </Col>
      </Row>
    </Header>
    <Row justify="space-around" type="flex">
      <Col span={24} className="carousel mb-50">
        <Carousel/>
      </Col>
      <Col span={20} className="card mb-50">
        <Row gutter={16}>
          <Col span={6} md={6} sm={12} xs={24}>
          { props.data.map(view => (
            <Card
              hoverable
              cover={
                <img alt="image1" style={{ width: '100%' }} src="/static/images/big-images-2.jpg" />
              }
              key={view.id}
              >
              <Card.Meta
                title={view.name}
                description="burger with patty and cheese"
              />
            </Card>
          ))}
            <Card/>           
          </Col>
        </Row>
      </Col>
    </Row>
    <ul>
      {props.data.map(view => (
        <li key={view.id}>
          <Link href="/p/[id]" as={`/p/${view.id}`}>
            <a>{view.name}</a>
          </Link>
        </li>
      ))}
    </ul>
    <Row>
        <WeatherAPP/>
    </Row>

    <Row justify="space-around" type="flex" style={{ background: '#f9f9f9' }}>
      <Col md={8} xs={20} style={{height: '40px', display: 'flex'}}>
        <div style={{ margin: 'auto 0px' }}>
          &copy; 2020 Dora Blog 
        </div>
      </Col>
      <Col md={8} xs={20}>
        <div style={{ margin: 'auto', height: '40px', display: 'flex', justifyContent: 'flex-end' }}>
          <Icon className="icon" type="facebook" />
          <Icon className="icon" type="instagram" />
          <Icon className="icon" type="google" />
        </div>
      </Col>
    </Row>
  </Layout>
);

Index.getInitialProps = async function() {
  const settings = {
    headers: {
        Accept: 'application/json',
    }
};
  const res = await fetch('https://www.travel.taipei/open-api/zh-tw/Attractions/All',settings);
  //const res = await fetch('https://api.tvmaze.com/search/shows?q=batman');
  const data = await res.json();

  console.log(data);

  return data;
};

export default Index;
