import { LOAD_DATA , DECREMENT_COUNTER, INCREMENT_COUNTER } from './actions';

const initialState = {
  list: [],
  value: 0
};

const counterReducer = (state = initialState, action) => {
  switch (action.type) {
      case INCREMENT_COUNTER:
          return {...state, value: state.value + 1};
      case DECREMENT_COUNTER:
          return {...state, value: state.value - 1};
      case LOAD_DATA:
          return {...state, list: state.value + 1} ;
      default:
          return {...state};
  }
};

export default counterReducer;
