import { Icon , Row , Col , Layout , Tag   } from 'antd';
import Link from 'next/link';
import "../assets/styles.less";
import Carousel from '../components/carousel';
import { connect } from 'react-redux';
import { decrementCounter, incrementCounter } from '../redux/actions';
import { loadDataSaga } from '../redux/saga';

const { Header } = Layout;

const IndexPage = props  => (
  <Layout>
  <Header>
    <Row justify="space-around" type="flex">
      <Col span={20}>
        <Row justify="space-around" type="flex">
          <Col span={12} md={12} xs={24}>
            <span className="ml-30 float-left"><Link href="/"><a>Welcome to Dora  Blog</a></Link></span>
          </Col>
          <Col span={12} md={12} xs={0}>
          </Col>
        </Row>
      </Col>
    </Row>
  </Header>
  <Row justify="space-around" type="flex">
    <Col span={24} className="carousel mb-50">
      <Carousel/>
    </Col>
    <Col span={20} className="card mb-50">
      <Row gutter={16}>
        {/* <Card></Card> */}
        {/* { props.data.map(view => (
          <Col span={8} md={8} sm={12} xs={24}>
            <Link href="/Attractions/[id]" as={`/Attractions/${view.id}`}>
            <Card
              hoverable
              key={view.id}
              title={view.name}
              className="card mb-50"
              >
              <ul>
                <li><span className="content-p">景點地址:</span>{view.address}</li>
                <li><span className="content-p">營業時間:</span>{view.open_time?view.open_time:'無'}</li>
                <li><span className="content-p">景點分類:</span>{view.category.map(e => <Tag color="magenta">{e.name}</Tag>)}</li>
              </ul>
            </Card>
            </Link>
          </Col>
        ))} */}
      </Row>
    </Col>
  </Row>
  <Row>
      {/* <WeatherAPP/> */}
      <button onClick={props.incrementCounter}>Increment</button>
     <button onClick={props.decrementCounter}>Decrement</button>
    <h1>{props.counter}</h1>
  </Row>

  <Row justify="space-around" type="flex" style={{ background: '#f9f9f9' }}>
    <Col md={8} xs={20} style={{height: '40px', display: 'flex'}}>
      <div style={{ margin: 'auto 0px' }}>
        &copy; 2020 Dora Blog 
      </div>
    </Col>
    <Col md={8} xs={20}>
      <div style={{ margin: 'auto', height: '40px', display: 'flex', justifyContent: 'flex-end' }}>
        <Icon className="icon" type="facebook" />
        <Icon className="icon" type="instagram" />
        <Icon className="icon" type="google" />
      </div>
    </Col>
  </Row>
</Layout>
)

// IndexPage.getInitialProps = async (props) => {
//   const { store, isServer } = props.ctx;
//   store.dispatch(loadDataSaga());
//   return { isServer };
// };

IndexPage.getInitialProps = async ({ store, isServer }) => {
  // store.dispatch(serverRenderClock(isServer))
  store.dispatch(loadDataSaga())
  return { isServer }
}

const mapStateToProps = state => ({
    counter: state.counter.value
});

const mapDispatchToProps = {
    incrementCounter: incrementCounter,
    decrementCounter: decrementCounter,
};
export default connect(mapStateToProps, mapDispatchToProps)(IndexPage);
