import { Card , Tag } from 'antd';
import "../assets/styles.less";
import { loadData } from '../redux/actions';
import { connect } from 'react-redux';

const Index = (props) => {
  return(
    <div>
      {props}
    </div>
    // <Card
    //   hoverable
    //   key={view.id}
    //   title={view.name}
    //   className="card mb-50"
    //   >
    //   <ul>
    //     <li><span className="content-p">景點地址:</span>{view.address}</li>
    //     <li><span className="content-p">營業時間:</span>{view.open_time?view.open_time:'無'}</li>
    //     <li><span className="content-p">景點分類:</span>{view.category.map(e => <Tag color="magenta">{e.name}</Tag>)}</li>
    //   </ul>
    // </Card>

  )
}


Index.getInitialProps = async (props) => {
  const { store, isServer } = props.ctx;
  if (!store.getState().viewList) {
    store.dispatch(loadData());
  }
  return { isServer };
};

const mapStateToProps = ({ viewList }) => ({ viewList });

export default connect(mapStateToProps)(Index);
