// };
//   const res = await fetch('https://www.travel.taipei/open-api/zh-tw/Attractions/All',settings);
//   const data = await res.json();
//   return data;
// };


IndexPage.getInitialProps = async ({ req }) => {
  const res = await getPosts()
  const data = await res.json()
  return data;
  
}

export default IndexPage;
