import { useRouter } from 'next/router';
import { Icon, Row , Col , Layout , Card } from 'antd';
import Link from 'next/link';
const { Header, Content } = Layout;

export default function View() {
  const router = useRouter();
  return (
    <Layout>
    <Row justify="space-around" type="flex">
      <Col span={24} className="carousel mb-50">
        <div>
          <h2>{ router.query.name }</h2>
          <p><Icon type="phone" />{ router.query.tel}</p>
          <p><Icon type="home"/>{ router.query.official_site}</p>
          <p><Icon type="dollar"/>{ router.query.ticket}</p>
          <p>{ router.query.introduction}</p>
        </div>
      </Col>
    </Row>
  </Layout>
  );
}
