import fetch from 'isomorphic-unfetch';
import "../assets/styles.less";
import { Layout ,Card , Row , Col ,Tag   } from 'antd';
import Link from 'next/link';
import Header from '../components/header';
import Footer from '../components/footer';
import Carousel from '../components/carousel';
import { getPosts } from '../api/index';

const { Content } = Layout;


const IndexPage = props => (
  <Layout>
    <Header />
    <Content>
      <Carousel/>
      <Row justify="space-around" type="flex">
      <Col span={20} className="card mb-50">
        <Row gutter={16}>
          { props.data.map(view => (
            <Col span={8} md={8} sm={12} xs={24}>
              <Card
                  hoverable
                  key={view.id}
                  title={view.name}
                  className="card mb-50"
                  post={view}
                  >
                  <ul>
                    <li><span className="content-p">景點地址:</span>{view.address}</li>
                    <li><span className="content-p">營業時間:</span>{view.open_time?view.open_time:'無'}</li>
                    <li><span className="content-p">景點分類:</span>{view.category.map(e => <Tag color="magenta">{e.name}</Tag>)}
                    </li>
                  </ul>
              </Card>
            </Col>
          ))}
          </Row>
      </Col>
      </Row>
    </Content>
    <Footer />
  </Layout> 
)

