// import { Card , Tag } from 'antd';
import { Card , Row , Col ,Tag  } from 'antd';
import Link from 'next/link';
import "../assets/styles.less";

const cardModule = ({post}) => {
  return(
    <Link href={{ pathname: '/post', query: { slug: post.id } }}>
    <Card
      hoverable
      key={post.id}
      title={post.name}
      className="card mb-50"
      >
      {post.name}
    </Card>
    </Link>
  )
}

