// export default Index


class CardList extends Component {
  
  constructor(props) {
    super(props);
    this.state = {
      dataList: []
    };
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    if (nextProps.userData && nextProps.userData !== prevState.dataSource) {
      return {
        dataList: nextProps.views
      };
    }
    return null; 
  }

  render() {
    const { dataList } = this.state;
    dataList.map(item => {
      item.key = item.id;
    });
    return (
      <div>
        {dataList}
      </div>
    //   <Card
    //   hoverable
    //   key={view.id}
    //   title={view.name}
    //   className="card mb-50"
