const cardModule = ({ title, placeholderData }) => {
  // const { images } = post;
  // let imageURL = images[0]?post.images[0].src:''
  console.log('placeholderData',JSON.parse(JSON.stringify(placeholderData.data)))
  // let data = JSON.parse(placeholderData.data);

  const [data, setData] = useState([]);

  useEffect(() => {
    if(placeholderData.data){
      setData(placeholderData)
    }
  }, [data])

  return(
    <div>
      <h1>{title}</h1>
      {data}
      {/* {placeholderData.data}
      { placeholderData && (
        <div>{placeholderData['data']}</div>
      )} */}


    </div>
  )
}

export default connect(state => state)(cardModule)
