import { connect } from 'react-redux';
import "../assets/styles.less";
import { Layout , Row , Col ,Tag ,Button  } from 'antd';
import Header from '../components/header';
import Footer from '../components/footer';
import Carousel from '../components/carousel';


const { Content } = Layout;
const PostPage = props => {
  const { detail, router } = props;
  console.log(props)
  const filterData = (view) =>{
    return view.filter(e => e.id == router.query.id)
  }
  console.log(filterData)
  return (
    <Layout>
    <Header />
    <Content>
      <Row justify="space-around" type="flex">
      <Col span={20} className="card mb-50">
        <Row gutter={16}>
        <div>{`store.value is ${filterData(detail)}`}</div>
        {/* <div>{`store.value is ${props.detail}`}</div> */}
          </Row>
      </Col>
      </Row>
    </Content>
    <Footer />
  </Layout> 
  )
}

const mapStateToProps = state => {
  return {
    detail: state.views.data,
  };
}

export default connect(mapStateToProps)(PostPage);
