import fetch from 'isomorphic-unfetch';
import { take, put, fork } from 'redux-saga/effects';
import { LOAD_DATA , loadData } from './actions';

export function* loadDataSaga () {
  console.log('555')
  const settings = {
    headers: {
        Accept: 'application/json',
    }
  }
  while (true) {
    yield take(LOAD_DATA);
    try {
      const res = yield fetch('https://www.travel.taipei/open-api/zh-tw/Attractions/All',settings);
      const data = yield res.json();
      yield put(loadData(data));
    } catch (e) {
      console.log('錯誤')
      //yield put(fetchUserListFail());
    }
  }
  // try {
