import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card } from 'antd';
import "../assets/styles.less";


const Index = () => {
  const [ data , setData ] = useState({ list:[]});
  const config = {
    headers: { 'Accept': 'application/json' }
  }
  useEffect(async () => {
    const res = await axios('https://www.travel.taipei/open-api/zh-tw/Attractions/All',config)
    console.log(res)
  },[])
  return(
    <Card
    hoverable
    cover={
      <img alt="image1" style={{ width: '100%' }} src="/static/images/big-images-2.jpg" />
    }
    >
    <Card.Meta
      title="Menu 1"
      description="burger with patty and cheese"
    />
  </Card>
  )
}

export default Index
