//       className="card mb-50"
//       >
//       <ul>
//         <li><span className="content-p">景點地址:</span>{view.address}</li>
//         <li><span className="content-p">營業時間:</span>{view.open_time?view.open_time:'無'}</li>
//         <li><span className="content-p">景點分類:</span>{view.category.map(e => <Tag color="magenta">{e.name}</Tag>)}</li>
//       </ul>
//     </Card>
//   )
// }


// export default Index


class CardList extends Component {
  
  constructor(props) {
    super(props);
    this.state = {
      dataList: []
    };
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    if (nextProps.userData && nextProps.userData !== prevState.dataSource) {
      return {
        dataList: nextProps.views
      };
    }
    return null; 
  }

  render() {
    const { dataList } = this.state;
    dataList.map(item => {
      item.key = item.id;
    });
    return (
      <Card
      hoverable
      key={view.id}
      title={view.name}
      className="card mb-50"
      >
      <ul>
        <li><span className="content-p">景點地址:</span>{view.address}</li>
        <li><span className="content-p">營業時間:</span>{view.open_time?view.open_time:'無'}</li>
        <li><span className="content-p">景點分類:</span>{view.category.map(e => <Tag color="magenta">{e.name}</Tag>)}</li>
      </ul>
    </Card>
    );
  }
}

export default CardList;
