import { Card , Tag } from 'antd';
import "../assets/styles.less";
//import { loadData } from '../redux/actions'

const Index = (props) => {
  console.log(props)
  return(
    // <Card
    //   hoverable
    //   key={view.id}
    //   title={view.name}
    //   className="card mb-50"
    //   >
    //   <ul>
    //     <li><span className="content-p">景點地址:</span>{view.address}</li>
    //     <li><span className="content-p">營業時間:</span>{view.open_time?view.open_time:'無'}</li>
    //     <li><span className="content-p">景點分類:</span>{view.category.map(e => <Tag color="magenta">{e.name}</Tag>)}</li>
    //   </ul>
    // </Card>
    // <Card
    //   hoverable
    //   key='0'
    //   title='test'
    //   className="card mb-50"
    //   >
    //   <ul>
    //     <li><span className="content-p">景點地址:</span>view.address</li>
    //     <li><span className="content-p">營業時間:</span>view.open_time?view.open_time:'無'}</li>
    //     <li><span className="content-p">景點分類:</span>/Tag>)}</li>
    //   </ul>
    // </Card>
    <div>
    { props.data.map(view => (
      <Col span={8} md={8} sm={12} xs={24}>
        <Link href="/Attractions/[id]" as={`/Attractions/${view.id}`}>
        <Card
          hoverable
          key={view.id}
          title={view.name}
          className="card mb-50"
          >
          <ul>
            <li><span className="content-p">景點地址:</span>{view.address}</li>
            <li><span className="content-p">營業時間:</span>{view.open_time?view.open_time:'無'}</li>
            <li><span className="content-p">景點分類:</span>{view.category.map(e => <Tag color="magenta">{e.name}</Tag>)}</li>
          </ul>
        </Card>
        </Link>
      </Col>
    ))}
  </div>
  )
}

Index.getInitialProps = async function() {
  const settings = {
    headers: {
        Accept: 'application/json',
    }
};
  const res = await fetch('https://www.travel.taipei/open-api/zh-tw/Attractions/All',settings);
  const data = await res.json();
  return data;
};

export default Index
