import { connect } from 'react-redux';
import "../assets/styles.less";
import { Layout , Row , Col ,Tag ,Button  } from 'antd';
import Header from '../components/header';
import Footer from '../components/footer';
import Carousel from '../components/carousel';


const { Content } = Layout;
const PostPage = props => {
  const { detail, router } = props;
  console.log(props)
  const filterData = (view) =>{
    return view.filter(e => e.id == router.query.id)
  }
  console.log(filterData)
  return (
    <Layout>
    <Header />
    <Content>
      <Row justify="space-around" type="flex">
      <Col span={20} className="card mb-50">
        <Row gutter={16}>
        <div>{`store.value is ${filterData(detail)}`}</div>
        </Row>
      </Col>
      </Row>
    </Content>
    <Footer />
  </Layout> 
  )
}

const mapStateToProps = state => {
  return {
    detail: state.views.data,
  };
}


PostPage.getInitialProps = async ({ store }) => {
  const data = '88888'
  store.dispatch({ type: "GETDATA", payload: {...data} }); // component will be able to read from store's state when rendered
  return data
}

PostPage.getInitialProps = async (props) => {
  const { store, isServer } = props.ctx;
  let userData;
  console.log(store.getState())
  // if (store.getState().user.list.list.length === 0) {
  //   const res = await fetch('https://jsonplaceholder.typicode.com/users');
  //   userData = await res.json();
  //   store.dispatch(fetchUserListDataSuccess(userData));
  // }
  // return { isServer };
};


export default connect(mapStateToProps)(PostPage);
