import React from 'react'
import { getPost } from '../api/index';

const PostPage = ({ post }) =>{
  return(
    <div>
      <h1>
        {post.name}
      </h1>
    </div>

  )
}


PostPage.getInitialProps = async ({ query }) => {
  const res = await getPost(query.slug)
  const json = await res.json()
  return { post: json[0] }
}

export default PostPage
