import { React , useState } from 'react';
import { Card } from 'antd';
const WeatherIndex = () => {
  const [currentWeather, setCurrentWeather] = useState({
    observationTime: '2019-10-02 22:10:00',
    locationName: '臺北市',
    description: '多雲時晴',
    temperature: 27.5,
    windSpeed: 0.3,
    humid: 0.88,
  });
  return (
    <div>
      <Card hoverable>
        <Card.Meta
          title="多雲時晴"
          description="多雲時晴"
        />
      </Card>
    </div>
  ) 
}

export default WeatherIndex
