import { all, call, put, takeLatest } from 'redux-saga/effects';
import { getPosts } from '../api/index';
import { actionTypes, failure, loadDataSuccess } from './actions';

function* loadDataSaga() {
  try {
    //const res = yield fetch('https://jsonplaceholder.typicode.com/users')
    const res = yield getPosts()
    const data = yield res.json()
    yield put(loadDataSuccess(data.data))
  } catch (err) {
    yield put(failure(err))
  }
}

function* updateDataSaga() {
  console.log('Test Result:');
  try {
    //const res = yield getPosts(2)
    const res = yield fetch('https://jsonplaceholder.typicode.com/users')
    const data = yield res.json()
    console.log('data',data)
    // yield put(loadDataSuccess(data.data))
  } catch (err) {
    yield put(failure(err))
  }
}

function* rootSaga() {
  yield all([takeLatest(actionTypes.LOAD_DATA, loadDataSaga),takeLatest(actionTypes.UPDATE_DATA, updateDataSaga)])
}

export default rootSaga
