import { connect } from 'react-redux';
import "../assets/styles.less";
import { Layout , Row , Col , Descriptions   } from 'antd';
import Header from '../components/header';
import Footer from '../components/footer';
import Carousel from '../components/carousel';


const { Content } = Layout;
const PostPage = props => {
  const { detail, router } = props;
  const viewDetail = detail.filter(e => e.id == router.query.id)[0]
  return (
    <Layout>
    <Header />
    <Content>
      <Row justify="space-around" type="flex">
      <Col span={20} className="card mb-50">
        <Row gutter={16}>
        <Descriptions title="User Info" layout="vertical">
        <Descriptions.Item label="UserName">{viewDetail.name}</Descriptions.Item>
        <Descriptions.Item label="Telephone">{viewDetail.open_time}</Descriptions.Item>
        <Descriptions.Item label="Live">{viewDetail.name}</Descriptions.Item>
        <Descriptions.Item label="Address" span={2}>
        {viewDetail.address}
        </Descriptions.Item>
        <Descriptions.Item label="Remark">{viewDetail.name}</Descriptions.Item>
      </Descriptions>

        </Row>
      </Col>
      </Row>
    </Content>
    <Footer />
  </Layout> 
  )
}

const mapStateToProps = state => {
  return {
    detail: state.views.data,
  };
}


export default connect(mapStateToProps)(PostPage);
