const PaginationModule = ({current , dispatch}) => {
  const onChangeEvent = (e) =>{
    console.log('e',e)
    dispatch(increment())
    //dispatch(updateData())
    // console.log('dispatch',dispatch)
  }
  return(
    <div>
      <Pagination 
        defaultCurrent={current} 
        defaultPageSize={30} 
        onChange={onChangeEvent} total={430}>
      </Pagination>
    </div>
  )
}

export default connect(state => state)(PaginationModule)
